      .then((res) => {
        const content = {
          type: "md",
          image: { ipfs_cid: res.body.cid },
        };
        const data = {
          post: {
            main: JSON.stringify(content),
          },
          index: {
            post: JSON.stringify([
              {
                key: "insta",
                value: {
                  type: "md",
                },
              },
              {
                key: "main",
                value: {
                  type: "md",
                },
              },
            ]),
          },
        };
        State.update({
          uploaded: true,
        });
        Social.set(data, {
          force: true,
          onCommit: () => {
            setTimeout(() => {
              State.update({
                uploaded: false,
              });
            }, 3500);
          },
        });
      })
      .finally(() => {
        State.update({
          loading: false,
        });
      });
  } else {
    State.update({
      img: null,
    });
